import React, { useState, useEffect } from "react";
import Form from "react-bootstrap/Form";
import Button from "react-bootstrap/Button";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Signup() {
   let navigate = useNavigate();
   let [firstName, setFirstName] = useState("");
   let [lastName, setLastName] = useState("");
   let [email, setEmail] = useState("");
   let [mobile, setMobile] = useState("");
   let [password, setPassword] = useState("");
   let [confirmPassword, setConfirmPassword] = useState("");
   let role = 2;
   let [passwordMatch, setMatch] = useState("");


  useEffect(() => {
    if (password === confirmPassword) setMatch(false);
    else setMatch(true);
  });

  let handleSubmit = async () => {
    let res = await axios.post("http://localhost:4000/users/signup", {
      firstName,
      lastName,
      email,
      mobile,
      password,
      role,
    });
    console.log(res);
    if (res.data.statusCode === 200) {
      navigate("/login");
    }
  };

  return (
    <div>
      <h1 className="text-center mt-4">SignUp Here</h1>
      <Form style={{ width: "30%" }} className="m-auto mt-3">
        <Form.Group className="mb-3" >
          <Form.Label>First Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter First Name"
            onChange={(e) => {
              setFirstName(e.target.value);
            }}
          />
        </Form.Group>
        <Form.Group className="mb-3">
          <Form.Label>Last Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter Last Name"
            onChange={(e) => {
              setLastName(e.target.value);
            }}
          />
        </Form.Group>
        <Form.Group className="mb-3" >
          <Form.Label>Mobile</Form.Label>
          <Form.Control
            type="text"
            placeholder="Enter Mobile"
            
            onChange={(e) => {
              setMobile(e.target.value);
            }}
          />
        </Form.Group>
        <Form.Group className="mb-3" >
          <Form.Label>Email address</Form.Label>
          <Form.Control
            type="email"
            placeholder="Enter Email"
            
            onChange={(e) => {
              setEmail(e.target.value);
            }}
          />
        </Form.Group>
        <Form.Group className="mb-3" >
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter Password"
            
            onChange={(e) => {
              setPassword(e.target.value);
            }}
          />
        </Form.Group>
        <Form.Group className="mb-3" c>
          <Form.Label>Confrim Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Enter Confirm Password"
            
            onChange={(e) => {
              setConfirmPassword(e.target.value);
            }}
          />
        </Form.Group>
        <Button
          variant="primary"
          type="submit"
          className="d-flex m-auto"
          onClick={() => {
            handleSubmit();
          }}
        >
          Submit
        </Button>
        {passwordMatch ? (
          <div className="text-center mt-4" style={{ color: "red" }}>
            Password Must Be Match!
          </div>
        ) : (
          <></>
        )}
      </Form>
      <div className="d-flex justify-content-center mt-4">
        <span className="text-center">Already have an account &nbsp;?</span>
        &nbsp;
        <a href="/login">Login</a>
      </div>
    </div>
  );
}

export default Signup;
