import React,{useContext} from "react";
import {rentContext} from '../App';
import {Link} from 'react-router-dom';
import RemoveIcon from "@mui/icons-material/Remove";
import AddIcon from "@mui/icons-material/Add"; 

function Cart() {

  let context = useContext(rentContext);

  let cartPrice = 0;

let handleRemove = (e) => {
  context.cart.splice(context.cart.indexOf(e), 1);
  context.setCartValue(context.cart.length);
};

 let handleMinus = (e) => {
   let index = context.cart.findIndex((c) => c.name === e.name);
   if (index !== -1) {
     context.cart[index]["q"] = context.cart[index]["q"] - 1;
     context.setCartValue(context.cart.length);
   }
 };

 let handlePlus = (e) => {
   let index = context.cart.findIndex((c) => c.name === e.name);
   if (index !== -1) {
     context.cart[index]["q"] += 1;
     context.setCartValue(context.cart.length);
   }
 };
  return (
    <div>
      {context.cart.length > 0 ? (
        <>
          {context.cart.map((e, i) => {
            cartPrice = cartPrice + Number(e.price * e.q);
            return (
              <>
                <div
                  className="grid2 text-center m-5 bg-white p-3 rounded"
                  key={i}
                >
                  <img className="img" src={e.img} alt="Canon"></img>
                  <br />
                  <br />
                  <span className="blockquote" style={{ fontSize: "18px" }}>
                    {e.name}
                  </span>
                  <hr />
                  <div className="d-flex flex-direction-row">
                    <p className="lead pt-2">&#x20B9;{e.price} / day</p>
                    <div>
                      <button
                        className="btn minus bg-danger text-light "
                        onClick={() => {
                          handleMinus(e);
                        }}
                      >
                        <RemoveIcon />
                      </button>
                      Qty:{e.q}
                      <button
                        className="btn plus bg-danger text-light"
                        onClick={() => {
                          handlePlus(e);
                        }}
                      >
                        <AddIcon />
                      </button>
                      <button
                        className="product-btn-remove btn-danger d-grid gap-2 d-md-flex justify-content-md-end"
                        onClick={() => {
                          handleRemove(e);
                        }}
                      >
                        Remove from Cart
                      </button>
                    </div>
                  </div>
                </div>
              </>
            );
          })}
          <div className="placeholder-wrapper row text-decoration-none">
            <div className="product-price-total text-center ">
              Total Pay : {cartPrice}
            </div>
            <Link to="/">
              <button
                className="product-btn-placeholder "
                onClick={() => {
                  context.setCart([]);
                  context.setCartValue(0);
                }}
              >
                Place Order
              </button>
            </Link>
          </div>
        </>
      ) : (
        <>
          <h4 className="text-center">Cart Is Empty!</h4>
        </>
      )}
    </div>
  );
}

export default Cart;
