import React,{useContext} from "react";
import {rentContext} from '../App';
import {Link} from 'react-router-dom'; 

function Cart() {

  let context = useContext(rentContext);

  let cartPrice = 0;


  return (
    <div>
      {context.cart.length > 0 ? (
        <>
          {context.cart.map((e, i) => {
              debugger;
            cartPrice = cartPrice + Number(e.price * e.q);
            return (
              <>
                <div
                  className="first text-center m-5 bg-white p-3 rounded"
                  key={i}
                >
                  <img className="img" src={e.img} alt="Canon"></img>
                  <br />
                  <br />
                  <span className="blockquote" style={{ fontSize: "18px" }}>
                    {e.name}
                  </span>
                  <hr />
                  <div className="d-flex flex-direction-row">
                    <p className="lead pt-2">&#x20B9;{e.price}</p>
                  </div>
                </div>
              </>
            );
          })}
          
        </>
      ) : (
        <>
          <h4 className="text-center">Cart Is Empty!</h4>
        </>
      )}
    </div>
  );
}

export default Cart;
