import React, { useContext } from "react";
import rentContext from "../App";
function Cart() {
  let context = useContext(rentContext);

  return (
    <div>
      <div>
        {data.map((e, i) => {
          return (
            <div className="first text-center m-5 bg-white p-3 rounded" key={i}>
              <img className="img" src={e.img} alt="Canon"></img>
              <br />
              <br />
              <span className="blockquote" style={{ fontSize: "18px" }}>
                {e.name}
              </span>
              <hr />
              <div className="d-flex flex-direction-row">
                <p className="lead pt-2">&#x20B9;{e.price} / day</p>
                <button
                  className="btn btn-outline-success ml-auto mt-2"
                  style={{
                    marginRight: "15px",
                    width: "110px",
                    height: "40px",
                  }}
                  onClick={() => {
                    let print = data.findIndex((c) => c.name === e.name);
                    console.log(print);
                    if (print === -1) {
                      data.cart.push(e);
                      data.setcartValue(data.cart.length);
                    }
                  }}
                >
                  Add to Cart
                </button>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Cart;
