import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import axios from "axios";
import { useNavigate } from "react-router-dom";

function Login() {
  let navigate = useNavigate();
  let [mobile, set] = useState("");
  let [password, setPassword] = useState("");
  let role = 2;

  let handleSubmit = async () => {
    let res = await axios.post("http://localhost:4000/users/login", {
      mobile,
      password,
      role,
    });
    console.log(res);
    if (res.data.statusCode === 200) {
      sessionStorage.setItem("token", res.data.token);
      sessionStorage.setItem("firstName", res.data.firstName);
      navigate("/");
    }
  };
  return (
    <div>
      <h1 className="text-center mt-5">Login Here</h1>
      <Form style={{ width: "30%" }} className="m-auto mt-3">
        <Form.Group className="mb-3" controlId="formBasicEmail">
          <Form.Label>Email address</Form.Label>
          <Form.Control
            type="email"
            placeholder="Enter email"
            onChange={(e) => {
              setEmail(e.target.value);
            }}
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Password</Form.Label>
          <Form.Control
            type="password"
            placeholder="Password"
            required
            onChange={(e) => {
              setPassword(e.target.value);
            }}
          />
        </Form.Group>
        <Button
          variant="primary"
          type="submit"
          className="d-flex m-auto"
          onClick={() => handleSubmit()}
        >
          Submit
        </Button>
      </Form>
      <div className="d-flex justify-content-center mt-4">
        <span className="text-center">Don't have an account &nbsp;?</span>&nbsp;
        <a href="/signup">SignUp</a>
      </div>
    </div>
  );
}

export default Login;
